class Average:    def __init__(self):        pass    @staticmethod    def average_value(arr: list) -> int:        """           нахождение среднего числа из списка        """        if not arr:            return 0        return sum(arr) / len(arr)    # Функция сравнивает средние значения двух списков    def comparison_numbers(self, arr1, arr2):        array1 = self.average_value(arr1)        array2 = self.average_value(arr2)        if array1 > array2:            return "Первый список имеет большее среднее значение"        if array2 > array1:            return "Второй список имеет большее среднее значение"        return "Средние значения равны"